

public class test {
    public static void main(String[] args) {
        // TODO Auto-generated method stub
        int[] arr = {8, 6, 5, 9};
        for (int i = 0, j = arr.length - 1; i < j; i++, j--) {
            int temp = arr[i];
            arr[i] = arr[j];
            arr[j] = temp;
        }
        for (int i = 0; i < arr.length; i++) {
            System.out.print(arr[i] + " ");
        }
    }
}
